import { useQuery } from '@apollo/client'
import styled from 'styled-components'
import { FiCircle, FiSquare, FiTriangle } from 'react-icons/fi'
import { GET_POOL_DATA, GET_SWAPS } from '../queries'
import { Tabs, Tab, Content } from './Tabs'
import { Tokens } from './Tokens'
import { Pools } from './Pools'
import { Swaps } from './Swaps'

const ContentBodyWrapper = styled.div`
  padding: 1.4rem;
`

const TabsWrapper = styled.div`
  display: flex;
  justify-content: center;

  .tab-wrapper {
    margin: 5px;
    padding: 2px 6px;
    cursor: pointer;
    font-weight: 500;
    color: #a2a2a2;
    font-size: 1rem;
    transition: all 0.1s ease-in-out;
    display: flex;
    align-items: center;

    svg {
      padding-right: 4px;
    }

    &:hover,
    &.active {
      color: #0a0a0a;
    }
  }
`

const ContentsWrapper = styled.div`
  padding-top: 1.4rem;
`

export const ContentBody: React.FC = () => {
  useQuery(GET_POOL_DATA, {
    variables: { orderBy: 'totalValueLockedUSD' },
  })

  useQuery(GET_SWAPS, {
    nextFetchPolicy: 'cache-and-network',
  })

  /* * total supply, tradeVolume, tradeVolumeUSD, tx(transaction)Count*/

  return (
    <ContentBodyWrapper>
      <Tabs>
        <TabsWrapper>
          <Tab id={0}>
            <FiCircle /> Top Pools
          </Tab>
          <Tab id={1}>
            <FiSquare /> Tokens
          </Tab>
          <Tab id={2}>
            <FiTriangle /> Transactions
          </Tab>
        </TabsWrapper>

        <ContentsWrapper>
          <Content id={0}>
            <Pools />
          </Content>
          <Content id={1}>
            <Tokens />
          </Content>
          <Content id={2}>
            <Swaps />
          </Content>
        </ContentsWrapper>
      </Tabs>
    </ContentBodyWrapper>
  )
}
